mapper.select:

	SELECT count(*)
		   FROM (
		   		select ROWNUM as rnum, SURVEY_SEQ, SURVEY_NAME,SURVEY_CONTENT, ANONYMITY_CHECK_CODE, 
		   		SURVEY_START_DATE,SURVEY_CLOSED_DATE, STATE_CODE 
		   		FROM (select SURVEY_SEQ, SURVEY_NAME,SURVEY_CONTENT, ANONYMITY_CHECK_CODE, 
		   		SURVEY_START_DATE,SURVEY_CLOSED_DATE, STATE_CODE  from survey_list where survey_name LIKE #{keyword}
		   		order by ROWNUM desc
		   			)
		   		)


	SELECT count(ROWNUM)
		   FROM (
		   		select ROWNUM as rnum, SURVEY_SEQ, SURVEY_NAME,SURVEY_CONTENT, ANONYMITY_CHECK_CODE, 
		   		SURVEY_START_DATE,SURVEY_CLOSED_DATE, STATE_CODE 
		   		FROM (select SURVEY_SEQ, SURVEY_NAME,SURVEY_CONTENT, ANONYMITY_CHECK_CODE, 
		   		SURVEY_START_DATE,SURVEY_CLOSED_DATE, STATE_CODE  from survey_list where survey_name LIKE #{keyword} and STATE_CODE = #{selection} 
		   		order by ROWNUM desc
		   	)
		   	
		   	)


	SELECT count(ROWNUM)
		   FROM (
		   		select ROWNUM as rnum, SURVEY_SEQ, SURVEY_NAME,SURVEY_CONTENT, ANONYMITY_CHECK_CODE, 
		   		SURVEY_START_DATE,SURVEY_CLOSED_DATE, STATE_CODE 
		   		FROM (select SURVEY_SEQ, SURVEY_NAME,SURVEY_CONTENT, ANONYMITY_CHECK_CODE, 
		   		SURVEY_START_DATE,SURVEY_CLOSED_DATE, STATE_CODE  from survey_list where survey_name LIKE #{keyword}
		   		order by SURVEY_START_DATE, ROWNUM  desc
		   	)
		  
		   	)
