mapper.select:
select count(ROWNUM) from ( SELECT rnum ,department_name ,grade_name ,employee_name ,SURVEY_SEQ FROM ( SELECT ROWNUM AS rnum ,survey_seq ,department_name ,grade_name ,employee_name
FROM ( SELECT distinct m.survey_seq ,oc.department_name ,g.grade_name ,ui.employee_name ,ui.employee_id
FROM grade g ,user_info ui ,mapping m ,ORGANIZATION_CHART oc WHERE m.APPRAISEE_ID = ui.employee_id AND g.grade_id = ui.grade_id and oc.department_id = ui.department_id
and ui.employee_name like '%' || '' || '%'
and oc.DEPARTMENT_NAME LIKE '%' || #{keyword} || '%'
and g.grade_name = #{selection2}
and g.grade_name = #{selection2}
and g.grade_name = #{selection2}
and g.grade_name = #{selection2}
and g.grade_name = #{selection2}
)
) ORDER BY ROWNUM DESC ) where survey_seq = #{surveySeq}
