mapper.update:
UPDATE SURVEY_LIST SET SURVEY_NAME=#{surveyName}
      ,SURVEY_CONTENT=#{surveyContent}
      ,ANONYMITY_CHECK_CODE=#{anonymityCheckCode}
      ,SURVEY_START_DATE=TO_CHAR(#{surveyStartDate},'YYYY.MM.dd')
      ,SURVEY_CLOSED_DATE=TO_CHAR(#{surveyClosedDate} , 'YYYY.MM.dd')
      ,UPDATE_DATE=sysdate  WHERE SURVEY_SEQ=#{surveySeq}

mapper.insert:

         SELECT ITEM_ID_SEQ.NEXTVAL FROM DUAL



		INSERT INTO SURVEY_ITEM (QUESTION_SEQ
		     ,ITEM_SEQ
		     ,ITEM_CONTENT
		     ,ITEM_SCORE
		     ,UPDATE_DATE)
		VALUES(#{questionSeq}
		     ,#{itemSeq}
		     ,#{itemContent}
		     ,#{itemScore}
		     ,SYSDATE)

mapper.select:
SELECT SURVEY_SEQ ,SURVEY_NAME ,SURVEY_CONTENT ,ANONYMITY_CHECK_CODE ,to_date(SURVEY_START_DATE, 'YYYY-MM-dd') as surveyStartDate ,to_date(SURVEY_CLOSED_DATE, 'YYYY-MM-dd') as surveyClosedDate ,STATE_CODE ,CODE_DETAIL_NAME FROM COMMON_DETAIL cd, ( SELECT ROWNUM AS rnum ,SURVEY_SEQ ,SURVEY_NAME ,SURVEY_CONTENT ,ANONYMITY_CHECK_CODE ,SURVEY_START_DATE ,SURVEY_CLOSED_DATE ,STATE_CODE FROM (SELECT SURVEY_SEQ ,SURVEY_NAME ,SURVEY_CONTENT ,ANONYMITY_CHECK_CODE ,SURVEY_START_DATE ,SURVEY_CLOSED_DATE ,STATE_CODE FROM SURVEY_LIST WHERE (SURVEY_START_DATE is null and SURVEY_NAME LIKE '%' || #{keyword} || '%' and STATE_CODE LIKE (CASE WHEN #{selection} = '30005' THEN '%' || '' || '%' ELSE '%' || #{selection} || '%' END) ) or SURVEY_START_DATE >= (CASE WHEN #{surveyStartDate} is null THEN sysdate-99999 WHEN #{surveyStartDate} is not null THEN to_date(to_char(#{surveyStartDate},'yyyy-MM-dd'),'yyyy-MM-dd') END) and
STATE_CODE LIKE (CASE WHEN #{selection} = '30005' THEN '%' || '' || '%' ELSE '%' || #{selection} || '%' END) and
SURVEY_NAME LIKE '%' || #{keyword} || '%' ORDER BY ROWNUM DESC ) WHERE ROWNUM <= #{endRowNo} ) WHERE cd.code_detail_id = state_code and rnum >= #{startRowNo}
