mapper.update:
UPDATE SURVEY_LIST SET SURVEY_NAME=#{surveyName}
      ,SURVEY_CONTENT=#{surveyContent}
      ,ANONYMITY_CHECK_CODE=#{anonymityCheckCode}
      ,SURVEY_START_DATE=TO_CHAR(#{surveyStartDate},'YYYY.MM.dd')
      ,SURVEY_CLOSED_DATE=TO_CHAR(#{surveyClosedDate} , 'YYYY.MM.dd')
      ,UPDATE_DATE=sysdate  WHERE SURVEY_SEQ=#{surveySeq}

mapper.select:
SELECT rnum ,department_name ,grade_name ,employee_name ,SURVEY_COMPLETE_YN ,SURVEY_SEQ FROM ( SELECT ROWNUM AS rnum ,survey_seq ,department_name ,grade_name ,employee_name ,survey_complete_yn FROM ( SELECT distinct m.survey_seq ,oc.department_name ,g.grade_name ,ui.employee_name ,ui.employee_id ,m.survey_complete_yn FROM grade g ,user_info ui ,mapping m ,ORGANIZATION_CHART oc WHERE m.rater_id = ui.employee_id and g.grade_id = ui.grade_id and oc.department_id = ui.department_id and (m.survey_complete_yn LIKE '%' || #{keyword} || '%' or ui.employee_name LIKE '%' || #{keyword} || '%' or g.GRADE_NAME LIKE '%' || #{keyword} || '%' or oc.DEPARTMENT_NAME LIKE '%' || #{keyword} || '%')
) where survey_seq = #{surveySeq} ORDER BY ROWNUM ASC )
WHERE rnum >= #{startRowNo} and rnum <= #{endRowNo}
